-- ----------------------------
-- Table structure for `pendaftaran`
-- ----------------------------
DROP TABLE IF EXISTS `pendaftaran`;
CREATE TABLE `pendaftaran` (
  `nik` varchar(45) NOT NULL DEFAULT '',
  `nama_lengkap` varchar(45) DEFAULT NULL,
  `nama_panggilan` varchar(45) DEFAULT NULL,
  `jenkel` varchar(34) DEFAULT NULL,
  `agama` varchar(34) DEFAULT NULL,
  `ttl` varchar(34) DEFAULT NULL,
  `anakke` varchar(12) DEFAULT NULL,
  `saudara` varchar(12) DEFAULT NULL,
  `alamat` text,
  `no_hp_ayah` varchar(16) DEFAULT NULL,
  `no_hp_ibu` varchar(16) DEFAULT NULL,
  `kelas` varchar(12) DEFAULT NULL,
  `sek_asal` varchar(34) DEFAULT NULL,
  `alamat_sek_asal` text,
  `gol` varchar(12) DEFAULT NULL,
